/* Question List Styles */
.question-list {
    max-height: 60vh;
    overflow-y: auto;
    padding: 10px 0;
}

.question-item {
    background: linear-gradient(135deg, var(--purplelight), var(--purplemed));
    border: 3px solid var(--bluemed);
    border-radius: 15px;
    padding: 15px 20px;
    margin-bottom: 15px;
    box-shadow: 0 4px 8px rgba(0, 0, 0, 0.2);
    position: relative;
    transition: transform 0.2s, box-shadow 0.2s;
}

.question-item:hover {
    transform: translateY(-2px);
    box-shadow: 0 6px 12px rgba(0, 0, 0, 0.3);
}

.question-item-header {
    display: flex;
    justify-content: space-between;
    align-items: center;
    margin-bottom: 10px;
}

.question-number {
    font-size: 1.8rem;
    font-weight: bold;
    color: var(--bluevlight);
}

.question-delete-btn {
    background: var(--redlight);
    color: white;
    border: none;
    border-radius: 50%;
    width: 35px;
    height: 35px;
    cursor: pointer;
    font-size: 1.6rem;
    transition: background 0.2s, transform 0.2s;
    display: flex;
    align-items: center;
    justify-content: center;
}

.question-delete-btn:hover {
    background: #c55552;
    transform: scale(1.1);
}

.question-text {
    color: var(--bluevlight);
    font-size: 1.6rem;
    margin-bottom: 8px;
    line-height: 1.4;
}

.question-answer {
    color: #FFD700;
    font-size: 1.5rem;
    font-weight: bold;
    margin-top: 8px;
}

.question-choices {
    color: var(--bluevlight);
    font-size: 1.4rem;
    margin-top: 8px;
    padding-left: 10px;
}

.question-choices div {
    margin: 3px 0;
}

/* Modal Form Styles */
#modal-tambah-soal .form-control {
    border: 2px solid var(--purplelight);
    border-radius: 10px;
    padding: 10px 15px;
    font-size: 1.5rem;
    transition: border-color 0.3s;
}

#modal-tambah-soal .form-control:focus {
    border-color: var(--purplemed);
    box-shadow: 0 0 0 0.2rem rgba(156, 112, 189, 0.25);
    outline: none;
}

#modal-tambah-soal .form-group {
    margin-bottom: 15px;
}

#modal-tambah-soal label {
    font-size: 1.6rem;
    margin-bottom: 5px;
    display: block;
}

/* Smaller font for question input buttons */
.btn-tambah-soal-custom {
    font-size: 1.8rem !important;
    padding: 1.2rem 2rem !important;
}

/* Different backgrounds for Essay and Multiple Choice */
.essay-item {
    background: linear-gradient(135deg, #FFE5B4, #FFDAB9) !important;
    border: 3px solid #FFA500 !important;
}

.essay-item .question-number {
    color: #8B4513 !important;
}

.essay-item .question-text,
.essay-item .question-answer {
    color: #4A4A4A !important;
}

.essay-item .question-answer {
    color: #FF6347 !important;
}

.pilgan-item {
    background: linear-gradient(135deg, #E0F7FA, #B2EBF2) !important;
    border: 3px solid #00BCD4 !important;
}

.pilgan-item .question-number {
    color: #006064 !important;
}

.pilgan-item .question-text,
.pilgan-item .question-choices {
    color: #424242 !important;
}

.pilgan-item .question-answer {
    color: #00796B !important;
}

/* Section backgrounds */
.essay-section {
    background: rgba(255, 248, 220, 0.3);
    border-radius: 10px;
    padding: 10px;
}

.pilgan-section {
    background: rgba(224, 247, 250, 0.3);
    border-radius: 10px;
    padding: 10px;
}

/* Modal Form Styles for Essay */
#modal-tambah-essay .form-control {
    border: 2px solid #FFA500;
    border-radius: 10px;
    padding: 10px 15px;
    font-size: 1.5rem;
    transition: border-color 0.3s;
}

#modal-tambah-essay .form-control:focus {
    border-color: #FF8C00;
    box-shadow: 0 0 0 0.2rem rgba(255, 165, 0, 0.25);
    outline: none;
}

#modal-tambah-essay .form-group {
    margin-bottom: 15px;
}

#modal-tambah-essay label {
    font-size: 1.6rem;
    margin-bottom: 5px;
    display: block;
}

/* Modal Form Styles for Pilgan */
#modal-tambah-pilgan .form-control {
    border: 2px solid #00BCD4;
    border-radius: 10px;
    padding: 10px 15px;
    font-size: 1.5rem;
    transition: border-color 0.3s;
}

#modal-tambah-pilgan .form-control:focus {
    border-color: #0097A7;
    box-shadow: 0 0 0 0.2rem rgba(0, 188, 212, 0.25);
    outline: none;
}

#modal-tambah-pilgan .form-group {
    margin-bottom: 15px;
}

#modal-tambah-pilgan label {
    font-size: 1.6rem;
    margin-bottom: 5px;
    display: block;
}

/* Scrollbar Styles for Question List */
.question-list::-webkit-scrollbar {
    width: 10px;
}

.question-list::-webkit-scrollbar-track {
    background: rgba(255, 255, 255, 0.1);
    border-radius: 10px;
}

.question-list::-webkit-scrollbar-thumb {
    background: var(--purplemed);
    border-radius: 10px;
}

.question-list::-webkit-scrollbar-thumb:hover {
    background: var(--purpledark);
}

