                if (isAdd) {
                    question = `Berat benda ${base} kg, lalu ditambah ${change} kg. Jadi beratnya ... kg.`;
                } else {
                    question = `Berat benda ${base} kg, lalu dikurangi ${change} kg. Sekarang beratnya ... kg.`;
